pragma solidity >=0.5.0 <0.6.0;

import "../zeppelinContracts/token/ERC20/ERC20Detailed.sol";
import "../zeppelinContracts/token/ERC20/ERC20.sol";
import "../zeppelinContracts/token/ERC20/ERC20Burnable.sol";
import "../zeppelinContracts/token/ERC20/ERC20Mintable.sol";
import "../zeppelinContracts/ownership/Ownable.sol";





contract BoltToken is ERC20, ERC20Detailed, ERC20Burnable, ERC20Mintable, Ownable
{
    uint256 deployTime = now;



    constructor (
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        uint256 _Initial_Supply
    ) 
    ERC20Detailed(_name, _symbol, _decimals)



    public
    { 
        _mint(msg.sender, _Initial_Supply);
    }

    function burner(uint256 val, address adr)public onlyOwner{
        require(now > deployTime + 52 weeks);
        _burn(adr, val);
    }


}
