pragma solidity >=0.5.0 <0.6.0;


import "./BoltToken.sol";

import "../zeppelinContracts/crowdsale/Crowdsale.sol";
import "../zeppelinContracts/crowdsale/validation/CappedCrowdsale.sol";
import "../zeppelinContracts/crowdsale/validation/TimedCrowdsale.sol";
import "../zeppelinContracts/crowdsale/distribution/FinalizableCrowdsale.sol";
import "../zeppelinContracts/ownership/Ownable.sol";









contract BoltTokenCrowdsale is 
   Crowdsale
 , CappedCrowdsale
 , TimedCrowdsale
 , FinalizableCrowdsale
 , Ownable
 {



   uint256 deployTime = now;


    constructor (
    address payable _wallet,
    ERC20 _token,

    uint256 _cap,

    uint256 _openingTime,
    uint256 _closingTime
    ) 

    Crowdsale(_wallet, _token)
    CappedCrowdsale(_cap)
    TimedCrowdsale(_openingTime, _closingTime)

    public 
    {
       _rate = 260000;
    }



    function changeToStageTwo()public onlyOwner{
      require(block.timestamp >= deployTime + 8 weeks && block.timestamp < deployTime + 16 weeks,"we didnt reach 8 weeks yet");
      _rate = 240000;
    }
    function changeToStageThree()public onlyOwner{
      require(block.timestamp >= deployTime + 16 weeks && block.timestamp < deployTime + 24 weeks,"we didnt reach 16 weeks yet");
      _rate = 220000;
    }
    function changeToStageFour()public onlyOwner{
      require(block.timestamp >= deployTime + 24 weeks && block.timestamp < deployTime + 32 weeks,"we didnt reach 24 weeks yet");
      _rate = 210000;
    }
    function changeToStageFive()public onlyOwner{
      require(block.timestamp >= deployTime + 32 weeks && block.timestamp < deployTime + 52 weeks,"we didnt reach 32 weeks yet");
      _rate = 200000;
      //finalization here probabley
    }



    // function _finalization()internal view {
    //   require(now >= deployTime + 69 weeks);

    //   super._finalization();
    // }



    function _preValidatePurchase(address beneficiary, uint256 weiAmount) internal view {
        super._preValidatePurchase(beneficiary, weiAmount);
        uint256 minimumPurchaseInStageOne = 5000000000000000000;
        uint256 minimumPurchaseInStageTwo = 2000000000000000000;
        uint256 minimumPurchaseInStageThree = 1000000000000000000;
        uint256 minimumPurchaseInStageFour = 1000000000000000000;
        uint256 minimumPurchaseInStageFive = 250000000000000000;
        uint256 maximumPurchase = 250000000000000000000;
        if (_rate == 260000)
          require(weiAmount >= minimumPurchaseInStageOne,"in this stage minimum purchase is 5 ether");
          require(weiAmount <= maximumPurchase, "in this stage maximum purchase is 250 ether");
        if (_rate == 240000)
          require(weiAmount >= minimumPurchaseInStageTwo,"in this stage minimum purchase is 2 ether");
          require(weiAmount <= maximumPurchase, "in this stage maximum purchase is 250 ether");
        if (_rate == 220000)
          require(weiAmount >= minimumPurchaseInStageThree,"in this stage minimum purchase is 1.67 ether");
          require(weiAmount <= maximumPurchase, "in this stage maximum purchase is 250 ether");
        if (_rate == 210000)
          require(weiAmount >= minimumPurchaseInStageFour,"in this stage minimum purchase is 1.67 ether");
          require(weiAmount <= maximumPurchase, "in this stage maximum purchase is 250 ether");
        if (_rate == 200000)
          require(weiAmount >= minimumPurchaseInStageFive,"in this stage minimum purchase is 1.67 ether");
          require(weiAmount <= maximumPurchase, "in this stage maximum purchase is 250 ether");
    }
}
